mapper.select:
	select 
	,
	cc.county_name as cc_county_name, hc.community_no, hc.community_name, ccw.name as website_name
	FROM house_second_hand_house h
	INNER JOIN house_community hc ON (hc.erp_id=h.community_id )
	INNER JOIN common_county cc ON (cc.erp_id=hc.region_id)
	LEFT JOIN common_cbd cbd ON (cbd.erp_id = hc.business_circle_id)
	LEFT JOIN common_cbd_website ccw ON (ccw.website_id = cbd.belong_to)

	INNER JOIN house_community_subway_station_mapping hcssm ON (hcssm.community_id=hc.erp_id)
	INNER JOIN common_subway_station css ON (css.erp_id=hcssm.subway_station_id AND css.erp_id=#{subway_station_id,jdbcType=VARCHAR})

	INNER JOIN common_subway cs ON (cs.erp_id=css.station_line_id AND cs.erp_id=#{station_line_id,jdbcType=VARCHAR})




	  AND (
	  price BETWEEN #{price[0],jdbcType=VARCHAR} AND #{price[1],jdbcType=VARCHAR} 
	  price=#{price[0],jdbcType=VARCHAR}
     )


	  AND (
	  area BETWEEN #{area[0],jdbcType=VARCHAR} AND #{area[1],jdbcType=VARCHAR} 
	  area=#{area[0],jdbcType=VARCHAR}
     )


	  AND (
	  shi BETWEEN #{shi[0],jdbcType=VARCHAR} AND #{shi[1],jdbcType=VARCHAR} 
	  shi=#{shi[0],jdbcType=VARCHAR}
     )


	  AND (
	  complate_year BETWEEN #{complate_year[0],jdbcType=VARCHAR} AND #{complate_year[1],jdbcType=VARCHAR} 
	  complate_year=#{shi[0],jdbcType=VARCHAR}
     )


	  AND ( orientation_no=#{orientation_no[0],jdbcType=VARCHAR} )


	  AND ( decoration_id=#{decoration_id[0],jdbcType=VARCHAR} )


	  AND ( tags like '%#{tags[0],jdbcType=VARCHAR}%')


    		AND (hc.erp_id = #{communityErpId[0]})


    	AND (h.house_id = #{house_id[0]})



      ORDER BY ${orderby[0],jdbcType=VARCHAR}

	select ,
	cc.erp_id as ct_erp_id, cc.county_name as ct_county_name,
	cbd.erp_id as cbd_erp_id, cbd.cbd_name as cbd_cbd_name,
	ccw.website_id as ccw_website_id, ccw.name as ccw_name,
	hc.erp_id as cm_erp_id, hc.community_no as cm_community_no, hc.community_name as cm_community_name, hc.build_year as cm_build_year,
	bk.erp_id as pb_erp_id, bk.agent_name as pb_agent_name, bk.telephone as pb_telephone,
	ori.orientation_no as or_orientation_no, ori.orientation_name as or_orientation_name,
	us.erp_id as us_erp_id, us.usage_name as us_usage_name
	FROM house_second_hand_house h
	INNER JOIN house_community hc ON (hc.erp_id=h.community_id AND hc.deleteflag=0)
	INNER JOIN common_cbd cbd ON (cbd.erp_id=hc.business_circle_id AND cbd.deleteflag=0)
	INNER JOIN common_county cc ON (cc.erp_id=hc.region_id AND cc.deleteflag=0)
	INNER JOIN common_cbd_website ccw ON (cbd.belong_to = ccw.website_id)
	LEFT JOIN b_broker bk ON (bk.erp_id=h.publisher_id AND bk.deleteflag=0)
	LEFT JOIN common_orientations ori ON (ori.orientation_no=h.orientation_no AND ori.deleteflag=0)
	LEFT JOIN common_usage us ON (us.erp_id=h.usage_id AND us.deleteflag=0)



			INNER JOIN house_community_subway_station_mapping hcssm ON (hcssm.community_id=hc.erp_id)
			INNER JOIN common_subway_station css ON (css.erp_id=hcssm.subway_station_id)


			INNER JOIN house_community_subway_station_mapping hcssm ON (hcssm.community_id=hc.erp_id)
			INNER JOIN common_subway_station css ON (css.erp_id=hcssm.subway_station_id AND css.erp_id=#{subway_station_id[0],jdbcType=VARCHAR})

			INNER JOIN common_subway cs ON (cs.erp_id=css.station_line_id AND cs.erp_id=#{station_line_id[0],jdbcType=VARCHAR})




			INNER JOIN house_community_subway_station_mapping hcssm ON (hcssm.community_id=hc.erp_id)
			INNER JOIN common_subway_station css ON (css.erp_id=hcssm.subway_station_id)



	  AND h.deleteflag = 0


	  AND (
	  price BETWEEN #{price[0],jdbcType=VARCHAR} AND #{price[1],jdbcType=VARCHAR} 
	  price=#{price[0],jdbcType=VARCHAR}
     )


	  AND (
	  area BETWEEN #{area[0],jdbcType=VARCHAR} AND #{area[1],jdbcType=VARCHAR} 
	  area=#{area[0],jdbcType=VARCHAR}
     )


	  AND (
	  shi BETWEEN #{shi[0],jdbcType=VARCHAR} AND #{shi[1],jdbcType=VARCHAR} 
	  shi=#{shi[0],jdbcType=VARCHAR}
     )


	  AND (
	  complate_year BETWEEN #{complate_year[0],jdbcType=VARCHAR} AND #{complate_year[1],jdbcType=VARCHAR} 
	  complate_year=#{shi[0],jdbcType=VARCHAR}
     )


	  AND ( h.orientation_no=#{orientation_no[0],jdbcType=VARCHAR} )


	  AND ( decoration_id=#{decoration_id[0],jdbcType=VARCHAR} )



	  AND (h.tags  & #{tags[0]} = #{tags[0]})


      AND (ccw.website_id = #{cbdId[0]})


      AND (cc.erp_id = #{countyId[0]})



      AND (h.storey_count BETWEEN #{floor[0]} AND #{floor[1]})


      AND (h.house_vervical_location = #{vervicalLocation[0]})



	AND (h.house_id = #{homePageBySearchField[0]}

    		 		OR cc.erp_id in 

    		 			#{listItem}



    		 		OR ccw.website_id in 

    		 			 #{listItem}



    		 		OR hc.erp_id in

    		 			 #{listItem}



    		 		OR css.erp_id in

    		 		  #{listItem}


    		 )



	   	AND	h.publisher_id = #{publisher_id[0]}



    	AND (hc.erp_id = #{communityErpId[0]})


    	AND (hc.erp_id = #{community[0]})



  		GROUP BY h.shelving_id


      ORDER BY ${orderby[0]}
